sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;

	function getRandomInt(min, max) {
    //debugger;
    return Math.floor(Math.random() * (max - min + 1)) + min;
	}

	function numberWithCommas(x) {
			return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
	}

	var opts = {
    lines: 12,
    angle: -0.1,
    lineWidth: 0.1,
    limitMax: "false",
    percentColors: [
        [0.0, "#C3E88D"],
        [0.25, "#89DDFF"],
        [0.50, "#FFCB6B"],
        [0.75, "#f07178"],
        [0.95, "#FF5370"]
    ], // !!!!
    strokeColor: "#161824",
    generateGradient: true,
    pointer: {
        length: 0.6, // // Relative to gauge radius
        strokeWidth: 0, // The thickness
        color: "#000000" // Fill color
    }
	};

	//if (document.getElementById("dial")) {
	if($('.dial')[0]){

			//var target1 = document.getElementById("dial");
		var target1 = $('.dial')[0];
		var dial1 = new Gauge(target1).setOptions(opts);

		dial1.setTextField(target1.nextElementSibling);
		dial1.maxValue = 300;
		dial1.animationSpeed = 15;
		dial1.set(getRandomInt(1, 300));
	}


}

