// Animating the numbers for each label
// http://d.pr/Uzze/5G73F7Jj
function rollCounters(delay) {
    anime({
        targets: ".red",
        innerHTML: getRandomInt(1, 100),
        round: 1,
        easing: "easeOutExpo",
        duration: 1750,
        delay: delay
    });

    anime({
        targets: ".blue",
        innerHTML: getRandomInt(1, 100),
        round: 1,
        easing: "easeOutExpo",
        duration: 1750,
        delay: delay
    });

    anime({
        targets: ".yellow",
        innerHTML: getRandomInt(1, 100),
        round: 1,
        easing: "easeOutExpo",
        duration: 1750,
        delay: delay
    });

    anime({
        targets: ".green",
        innerHTML: getRandomInt(1, 100),
        round: 1,
        easing: "easeOutExpo",
        duration: 1750,
        delay: delay
    });

    anime({
        targets: ".team-sales",
        innerHTML: numberWithCommas(getRandomInt(50000, 99999)),
        round: 1,
        easing: "easeOutExpo",
        duration: 3000
    });    

    anime({
        targets: ".total-sales",
        innerHTML: numberWithCommas(getRandomInt(100000, 500000)),
        round: 1,
        easing: "easeOutExpo",
        duration: 3000
    });

}

setTimeout(rollIn, 450);

function rollIn(){
// Fades in each row when the page loads
// http://d.pr/rYwL/57gkKHGv
var statdivs = anime({
    targets: ".stat",
    top: 0,
    duration: 2000,
    opacity: 1,
    easing: "easeOutQuint",
    elasticity: 1000,
    delay: function(el, i, l) {
        return i * 150;
    },
    begin: function() {
        rollCounters(150);
        // Delay the labeled integers so it coindices
        // with the staggered animations
    }
});

var statdivs = anime({
    targets: ".stagger",
    translateY: -150,
    offset: -150,
    duration: 2000,
    opacity: 1,
    easing: "easeOutQuint",
    elasticity: 1000,
    delay: function(el, i, l) {
        return i * 150;
    },
    begin: function() {
        rollCounters(150);
    }
});

}
